import React, { useState, useEffect } from "react";
import axios from "axios";
import HRSidebar from "../components/sidebar/HRSidebar";
import { Modal, Button } from "react-bootstrap";

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [managers, setManagers] = useState([]); // Managers list for assignment
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [newEmployee, setNewEmployee] = useState({
    name: "",
    surname: "",
    email: "",
    role: "employee",
    manager: "", // New field for manager assignment
  });

  // Fetch employees and managers on mount
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5002/api/users", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(response.data);
      } catch (err) {
        console.error("Error fetching employees:", err);
        setError("Failed to load employee data.");
      } finally {
        setLoading(false);
      }
    };

    const fetchManagers = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          "http://localhost:5002/api/users?role=manager",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setManagers(response.data);
      } catch (error) {
        console.error("Error fetching managers:", error);
      }
    };

    fetchEmployees();
    fetchManagers();
  }, []);

  // Assign manager to an employee
  const handleAssignManager = async (employeeId, managerId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:5002/api/users/${employeeId}/assign-manager`,
        { managerId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setEmployees((prevEmployees) =>
        prevEmployees.map((emp) =>
          emp._id === employeeId ? { ...emp, manager: managerId } : emp
        )
      );
    } catch (error) {
      console.error("Error assigning manager:", error);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <HRSidebar />
      <div className="container">
        <h2>Employee List</h2>

        <table className="table table-striped table-hover mt-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Surname</th>
              <th>Email</th>
              <th>Role</th>
              <th>Manager</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.name}</td>
                <td>{employee.surname}</td>
                <td>{employee.email}</td>
                <td>{employee.role}</td>
                <td>
                  <select
                    value={employee.manager || ""}
                    onChange={(e) =>
                      handleAssignManager(employee._id, e.target.value)
                    }
                    className="form-select"
                  >
                    <option value="">Unassigned</option>
                    {managers.map((manager) => (
                      <option key={manager._id} value={manager._id}>
                        {manager.name}
                      </option>
                    ))}
                  </select>
                </td>
                <td>
                  <Button
                    variant="warning"
                    onClick={() => setNewEmployee(employee)}
                    size="sm"
                  >
                    Edit
                  </Button>
                  <Button
                    variant="danger"
                    onClick={() => handleDelete(employee._id)}
                    size="sm"
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmployeeList;

