import React, { useState, useEffect } from "react";
import axios from "axios";
import HRSidebar from "../components/sidebar/HRSidebar";

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editEmployee, setEditEmployee] = useState(null); // Track employee being edited
  const [userRole, setUserRole] = useState(""); // Store logged-in user role

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5002/api/users", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setEmployees(response.data);
        setUserRole(localStorage.getItem("role")); // Assume role is stored in localStorage after login
      } catch (err) {
        console.error("Error fetching employees:", err);
        setError("Failed to load employee data.");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, []);

  // Handle update request
  const handleUpdate = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `http://localhost:5002/api/users/${id}`,
        {
          name: editEmployee.name,
          surname: editEmployee.surname,
          email: editEmployee.email,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setEmployees(
        employees.map((emp) =>
          emp._id === id ? { ...emp, ...editEmployee } : emp
        )
      );
      setEditEmployee(null); // Close edit mode
    } catch (err) {
      console.error("Error updating employee:", err);
      alert("Failed to update employee.");
    }
  };

  // Handle delete request
  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5002/api/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEmployees(employees.filter((emp) => emp._id !== id));
    } catch (err) {
      console.error("Error deleting employee:", err);
      alert("Failed to delete employee.");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <HRSidebar />
      <div className="container">
        <h2>Employee List</h2>
        <table className="table table-striped table-hover">
          <thead>
            <tr>
              <th>Name</th>
              <th>Surname</th>
              <th>Email</th>
              <th>Role</th>
              {userRole === "hr" && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                {editEmployee && editEmployee._id === employee._id ? (
                  <>
                    <td>
                      <input
                        type="text"
                        value={editEmployee.name}
                        onChange={(e) =>
                          setEditEmployee({
                            ...editEmployee,
                            name: e.target.value,
                          })
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        value={editEmployee.surname}
                        onChange={(e) =>
                          setEditEmployee({
                            ...editEmployee,
                            surname: e.target.value,
                          })
                        }
                      />
                    </td>
                    <td>
                      <input
                        type="email"
                        value={editEmployee.email}
                        onChange={(e) =>
                          setEditEmployee({
                            ...editEmployee,
                            email: e.target.value,
                          })
                        }
                      />
                    </td>
                    <td>{employee.role}</td>
                    {userRole === "hr" && (
                      <td>
                        <button
                          onClick={() => handleUpdate(employee._id)}
                          className="btn btn-primary btn-sm"
                        >
                          Save
                        </button>
                        <button
                          onClick={() => setEditEmployee(null)}
                          className="btn btn-secondary btn-sm"
                        >
                          Cancel
                        </button>
                      </td>
                    )}
                  </>
                ) : (
                  <>
                    <td>{employee.name}</td>
                    <td>{employee.surname}</td>
                    <td>{employee.email}</td>
                    <td>{employee.role}</td>
                    {userRole === "hr" && (
                      <td>
                        <button
                          onClick={() => setEditEmployee(employee)}
                          className="btn btn-warning btn-sm"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDelete(employee._id)}
                          className="btn btn-danger btn-sm"
                        >
                          Delete
                        </button>
                      </td>
                    )}
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EmployeeList;
