import React, { useEffect, useState } from "react";
import axios from "axios";
import ManagerSidebar from "../components/sidebar/ManagerSidebar";
import "./Manager.css";

const ManagerDashboard = () => {
  const [managerName, setManagerName] = useState("Manager");

  useEffect(() => {
    // Fetch manager's name from an API or local storage on component mount
    const fetchManagerName = async () => {
      try {
        const managerId = localStorage.getItem("userId"); // Assume managerId is stored after login
        const response = await axios.get(
          `http://localhost:5002/api/users/${managerId}`
        );
        setManagerName(response.data.name);
      } catch (error) {
        console.error("Error fetching manager's name:", error);
      }
    };
    fetchManagerName();
  }, []);

  return (
    <div className="container-grid">
      <aside className="navbar-left">
        <ManagerSidebar />
      </aside>

      <main className="main-content">
        <div className="welcome-container">
          <h1 className="welcome-message">Welcome, {managerName}!</h1>
          <p className="welcome-description">
            Here you can manage your team, approve leave, schedule shifts, and
            more.
          </p>
        </div>

        <div className="dashboard-cards">
          <div className="dashboard-card">
            <i className="fa fa-history"></i>
            <h3>Leave History</h3>
            <p>View detailed leave history for your employees.</p>
          </div>

          <div className="dashboard-card">
            <i className="fa fa-calendar-alt"></i>
            <h3>Shift Scheduling</h3>
            <p>
              Manage your team's shifts effectively and ensure proper
              scheduling.
            </p>
          </div>

          <div className="dashboard-card">
            <i className="fa fa-user-graduate"></i>
            <h3>Career Development</h3>
            <p>Track career development programs and performance reviews.</p>
          </div>

          <div className="dashboard-card">
            <i className="fa fa-bullhorn"></i>
            <h3>Announcements</h3>
            <p>Keep up-to-date with the latest company announcements.</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default ManagerDashboard;
