// src/hr/PostedCareers.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const PostedCareers = ({ openModal, setEditPosition }) => {
  const [positions, setPositions] = useState([]);

  useEffect(() => {
    fetchPositions();
  }, []);

  const fetchPositions = async () => {
    try {
      const response = await axios.get("http://localhost:5002/api/careers", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setPositions(response.data);
    } catch (error) {
      console.error("Error fetching positions:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5002/api/careers/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setPositions(positions.filter((position) => position._id !== id));
    } catch (error) {
      console.error("Error deleting position:", error);
    }
  };

  const handleEdit = (position) => {
    setEditPosition(position);
    openModal();
  };

  return (
    <div className="table-wrapper">
      <h2>Manage Posted Careers</h2>
      <table className="table table-striped table-hover">
        <thead>
          <tr>
            <th>Title</th>
            <th>Department</th>
            <th>Description</th>
            <th>Duration</th>
            <th>Deadline</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {positions.map((position) => (
            <tr key={position._id}>
              <td>{position.title}</td>
              <td>{position.department}</td>
              <td>{position.description}</td>
              <td>{position.duration} days</td>
              <td>
                {new Date(position.applicationDeadline).toLocaleDateString()}
              </td>
              <td>
                <button
                  onClick={() => handleEdit(position)}
                  className="btn btn-warning btn-sm"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(position._id)}
                  className="btn btn-danger btn-sm"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PostedCareers;
