import React, { useEffect, useState } from "react";
import axios from "axios";
import ManagerSidebar from "../components/sidebar/ManagerSidebar";
import "./Manager.css";

const ManagerDashboard = () => {
  const [managerName, setManagerName] = useState("Manager");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchManagerName = async () => {
      try {
        const userId = localStorage.getItem("userId");

        if (!userId) {
          throw new Error("User ID not found. Please log in again.");
        }

        const response = await axios.get(
          `http://localhost:5002/api/users/${userId}`
        );
        setManagerName(response.data.name);
      } catch (error) {
        console.error("Error fetching manager's name:", error);
        setError("Failed to load manager's name. Please try again.");
      }
    };
    fetchManagerName();
  }, []);

  return (
    <div className="container-grid">
      <aside className="navbar-left">
        <ManagerSidebar />
      </aside>

      <main className="main-content">
        <div className="welcome-container">
          {error ? (
            <p className="error-message">{error}</p>
          ) : (
            <h1 className="welcome-message">Welcome, {managerName}!</h1>
          )}
          <p className="welcome-description">
            Here you can manage your team, approve leave, schedule shifts, and
            more.
          </p>
        </div>

        <div className="dashboard-cards">{/* Dashboard card components */}</div>
      </main>
    </div>
  );
};

export default ManagerDashboard;
