// EMPLOYEES/LeaveManagement.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./LeaveManagement.css"; // Assume custom CSS for layout

const LeaveManagement = () => {
  const [leaveBalance, setLeaveBalance] = useState(null);
  const [leaveHistory, setLeaveHistory] = useState([]);
  const [upcomingEvents, setUpcomingEvents] = useState([]);

  useEffect(() => {
    fetchLeaveData();
    fetchUpcomingEvents(); // Fetch upcoming HR events or leave approvals
  }, []);

  // Fetch leave balance and history
  const fetchLeaveData = async () => {
    try {
      const [balanceResponse, historyResponse] = await Promise.all([
        axios.get("http://localhost:5002/api/leaves/balance", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }),
        axios.get("http://localhost:5002/api/leaves/history", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }),
      ]);

      setLeaveBalance(balanceResponse.data.leaveBalance);
      setLeaveHistory(historyResponse.data);
    } catch (error) {
      console.error("Error fetching leave data:", error);
    }
  };

  // Fetch upcoming events or pending/approved leave applications
  const fetchUpcomingEvents = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5002/api/events/upcoming",
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      setUpcomingEvents(response.data);
    } catch (error) {
      console.error("Error fetching upcoming events:", error);
    }
  };

  return (
    <div className="leave-management-container">
      <div className="main-content">
        <h2>Leave Management</h2>

        {/* Leave Balance Section */}
        <div className="leave-balance">
          <h3>Leave Balance</h3>
          <p>{leaveBalance !== null ? leaveBalance : "Loading..."}</p>
        </div>

        {/* Leave History Section */}
        <div className="leave-history">
          <h3>Leave History</h3>
          <ul>
            {leaveHistory.length > 0 ? (
              leaveHistory.map((leave) => (
                <li key={leave._id}>
                  {leave.leaveType} from {leave.startDate} to {leave.endDate} -{" "}
                  <strong>{leave.status}</strong>
                </li>
              ))
            ) : (
              <p>No leave history available.</p>
            )}
          </ul>
        </div>
      </div>

      {/* Right Sidebar */}
      <div className="right-sidebar">
        <h3>Upcoming Events</h3>
        <ul>
          {upcomingEvents.length > 0 ? (
            upcomingEvents.map((event, index) => (
              <li key={index}>
                <strong>{event.name}</strong> on {event.date} - {event.status}
              </li>
            ))
          ) : (
            <p>No upcoming events or leave statuses available.</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default LeaveManagement;
