// frontend/src/pages/HrTraining.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const HrTraining = () => {
  const [trainingPrograms, setTrainingPrograms] = useState([]);
  const [editingProgram, setEditingProgram] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    duration: "",
  });

  useEffect(() => {
    fetchTrainingPrograms();
  }, []);

  const fetchTrainingPrograms = async () => {
    try {
      const response = await axios.get("/api/training/programs");
      setTrainingPrograms(response.data);
    } catch (err) {
      console.error("Error fetching training programs:", err);
    }
  };

  const handleEditClick = (program) => {
    setEditingProgram(program._id);
    setFormData({
      name: program.name,
      description: program.description,
      duration: program.duration,
    });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`/api/training/programs/${editingProgram}`, formData);
      fetchTrainingPrograms(); // Refresh the program list
      setEditingProgram(null); // Exit edit mode
    } catch (err) {
      console.error("Error updating training program:", err);
    }
  };

  return (
    <div>
      <h2>Training Programs</h2>
      <ul>
        {trainingPrograms.map((program) => (
          <li key={program._id}>
            <h3>{program.name}</h3>
            <p>{program.description}</p>
            <p>{program.duration}</p>
            <button onClick={() => handleEditClick(program)}>Edit</button>
          </li>
        ))}
      </ul>

      {editingProgram && (
        <div>
          <h3>Edit Training Program</h3>
          <form onSubmit={handleSave}>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Program Name"
            />
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Description"
            ></textarea>
            <input
              type="text"
              name="duration"
              value={formData.duration}
              onChange={handleChange}
              placeholder="Duration"
            />
            <button type="button" onClick={handleSave}>
              Save
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default HrTraining;
