import React, { useState, useEffect } from "react";
import EmployeeSidebar from "../components/sidebar/EmployeeSidebar";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const EmployeeTraining = () => {
  const [trainings, setTrainings] = useState([]);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchAssignedTrainings = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          "http://localhost:5002/api/training/assignments",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTrainings(response.data);
      } catch (err) {
        console.error("Error fetching assigned trainings:", err);
        setError("Failed to load assigned training programs.");
      }
    };

    fetchAssignedTrainings();
  }, []);

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentTrainings = trainings.slice(indexOfFirstItem, indexOfLastItem);

  const nextPage = () => setCurrentPage(currentPage + 1);
  const prevPage = () => setCurrentPage(currentPage - 1);

  if (error) return <div>{error}</div>;

  return (
    <div className="app-container">
      <EmployeeSidebar />
      <div className="main-content">
        <h1>Assigned Training Programs</h1>
        <p>View all training programs that have been assigned to you.</p>

        <div className="container mt-4">
          <table className="table table-striped table-hover mt-3">
            <thead>
              <tr>
                <th>Training Program</th>
                <th>Description</th>
                <th>Duration</th>
                <th>Sessions</th>
              </tr>
            </thead>
            <tbody>
              {currentTrainings.map((assignment) => (
                <tr key={assignment._id}>
                  <td>{assignment.training.name}</td>
                  <td>{assignment.training.description}</td>
                  <td>{assignment.training.duration}</td>
                  <td>
                    {assignment.sessions.map((session, index) => (
                      <div key={index}>
                        {session.date}: {session.startTime} - {session.endTime}
                      </div>
                    ))}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination Controls */}
          <div className="pagination d-flex justify-content-between">
            <button
              onClick={prevPage}
              disabled={currentPage === 1}
              className="btn btn-secondary"
            >
              Previous
            </button>
            <span>Page {currentPage}</span>
            <button
              onClick={nextPage}
              disabled={indexOfLastItem >= trainings.length}
              className="btn btn-secondary"
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeTraining;
