// src/employees/Training.js
import React, { useState, useEffect } from "react";
import EmployeeSidebar from "../components/sidebar/EmployeeSidebar";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const Training = () => {
  const [trainings, setTrainings] = useState([]);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchTrainings = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          "http://localhost:5002/api/training/available", // Update the endpoint to get all available trainings
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setTrainings(response.data);
      } catch (err) {
        console.error("Error fetching trainings:", err);
        setError("Failed to load available trainings.");
      }
    };

    fetchTrainings();
  }, []);

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentTrainings = trainings.slice(indexOfFirstItem, indexOfLastItem);

  const nextPage = () => setCurrentPage(currentPage + 1);
  const prevPage = () => setCurrentPage(currentPage - 1);

  if (error) return <div>{error}</div>;

  return (
    <div className="app-container">
      <EmployeeSidebar />
      <div className="main-content">
        <h1>Training Programs</h1>
        <p>
          Here you can view all available training programs and enroll in new
          courses.
        </p>

        <div className="container mt-4">
          <table className="table table-striped table-hover mt-3">
            <thead>
              <tr>
                <th>Training Program</th>
                <th>Description</th>
                <th>Duration</th>
                <th>Available Sessions</th>
              </tr>
            </thead>
            <tbody>
              {currentTrainings.map((training) => (
                <tr key={training._id}>
                  <td>{training.name}</td>
                  <td>{training.description}</td>
                  <td>{training.duration}</td>
                  <td>
                    {training.sessions.map((session, index) => (
                      <div key={index}>
                        {session.date}: {session.startTime} - {session.endTime}
                      </div>
                    ))}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            <button onClick={prevPage} disabled={currentPage === 1}>
              Previous
            </button>
            <button
              onClick={nextPage}
              disabled={indexOfLastItem >= trainings.length}
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Training;
