import React, { useEffect, useState } from "react";
import axios from "axios";
import ManagerSidebar from "../components/sidebar/ManagerSidebar"; // Ensure path is correct

const ManagerLeave = () => {
  const [leaveApplications, setLeaveApplications] = useState([]);

  useEffect(() => {
    fetchLeaveApplications();
  }, []);

  const fetchLeaveApplications = async () => {
    try {
      const response = await axios.get(
        "http://localhost:5002/api/leaves/view",
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      setLeaveApplications(response.data);
    } catch (error) {
      console.error("Error fetching leave applications:", error);
    }
  };

  return (
    <div className="d-flex">
      <ManagerSidebar />
      <div className="container mt-4">
        <h2>Manage Leave Applications</h2>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Employee</th>
              <th>Leave Type</th>
              <th>Start Date</th>
              <th>End Date</th>
              <th>Document</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {leaveApplications.map((leave) => (
              <tr key={leave._id}>
                <td>{leave.employeeId}</td>
                <td>{leave.leaveType}</td>
                <td>{leave.startDate}</td>
                <td>{leave.endDate}</td>
                <td>
                  {leave.documentUrl ? (
                    <a
                      href={leave.documentUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      View
                    </a>
                  ) : (
                    "N/A"
                  )}
                </td>
                <td>{leave.status}</td>
                <td>
                  <button
                    className="btn btn-success me-2"
                    onClick={() => handleReview(leave._id, "Approved")}
                  >
                    Approve
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleReview(leave._id, "Declined")}
                  >
                    Decline
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="right-sidebar">
        {" "}
        {/* Right Sidebar Component */}
        <h4>Upcoming Events</h4>
        {/* Add dynamic content here */}
      </div>
    </div>
  );
};

export default ManagerLeave;
